export var refundPolicy = "\uCDE8\uC18C \uBC0F \uD658\uBD88\uADDC\uC815(2019. 2. 26 \uBD80\uD130 \uC720\uD6A8)\n\uC81C1\uC870 (\uBAA9\uC801 \uBC0F \uC801\uC6A9\uB300\uC0C1)\n\u2460 \uBCF8 \uC57D\uAD00\uC740 \uC8FC\uC2DD\uD68C\uC0AC (JANDA)(\uC774\uD558 \u2018\uD68C\uC0AC\u2019\uB77C \uD569\uB2C8\uB2E4)\uAC00 \uC81C\uACF5\uD558\uB294 \uC804\uC790\uC9C0\uAE09\uACB0\uC81C\uB300\uD589\uC11C\uBE44\uC2A4, \uC120\uBD88\uC804\uC790\uC9C0\uAE09\uC218\uB2E8\uC758 \uBC1C\uD589 \uBC0F \uAD00\uB9AC\uC11C\uBE44\uC2A4(\uC774\uD558 \u2018\uC804\uC790\uAE08\uC735\uAC70\uB798\uC11C\uBE44\uC2A4\u2019\uB77C \uD569\uB2C8\uB2E4)\uB97C \uCDE8\uC18C/\uD658\uBD88\uD568\uC5D0 \uC788\uC5B4, \uD68C\uC0AC\uC640 \uD68C\uC6D0\uAC04 \uAD8C\uB9AC, \uC758\uBB34 \uBC0F \uD68C\uC6D0\uC758 \uC774\uC6A9\uC808\uCC28 \uB4F1\uC5D0 \uAD00\uD55C \uC0AC\uD56D\uC744 \uADDC\uC815\uD558\uB294 \uAC83\uC744 \uADF8 \uBAA9\uC801\uC73C\uB85C \uD569\uB2C8\uB2E4.\n\u2461 \uD68C\uC0AC\uB294 \uD1B5\uC2E0\uD310\uB9E4\uC911\uAC1C\uC790\uC774\uBA70 \uAC1C\uC124\uB41C \uD589\uC0AC\uC758 \uC8FC\uCD5C \uBC0F \uB2F9\uC0AC\uC790\uAC00 \uC544\uB2D9\uB2C8\uB2E4.\n\u2462 \uD68C\uC0AC\uAC00 \uC81C\uACF5\uD558\uB294 \uACB0\uC81C\uC11C\uBE44\uC2A4\uB97C \uC774\uC6A9\uD55C \uC720\uB8CC\uD589\uC0AC\uB294 \uACB0\uC81C\uAE08\uC561\uC758 \uD658\uBD88 \uC2DC \uD68C\uC0AC\uAC00 \uC815\uD55C \uCDE8\uC18C/\uD658\uBD88\uC57D\uAD00\uC5D0 \uB530\uB985\uB2C8\uB2E4.\n\u2463 \uAC1C\uC124\uC790 \uD1B5\uC7A5\uC785\uAE08 \uBC29\uC2DD, \uC678\uBD80/\uD604\uC7A5\uC2E0\uCCAD \uBC29\uC2DD\uC744 \uD1B5\uD55C \uACB0\uC81C\uAE08\uC561\uC758 \uD658\uBD88\uC740 \uBCF8 \uC57D\uAD00\uC758 \uC801\uC6A9\uB300\uC0C1\uC5D0 \uD574\uB2F9\uD558\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.\n\uC81C2\uC870 (\uC815\uC758)\n\u2460 \uBCF8 \uC57D\uAD00\uC5D0\uC11C \uC815\uD55C \uC6A9\uC5B4\uC758 \uC815\uC758\uB294 \uC544\uB798\uC640 \uAC19\uC2B5\uB2C8\uB2E4.\n1. \u2018\uC804\uC790\uAE08\uC735\uAC70\uB798\u2019\uB77C \uD568\uC740 \uD68C\uC0AC\uAC00 \uC804\uC790\uC801 \uC7A5\uCE58\uB97C \uD1B5\uD558\uC5EC \uC804\uC790\uAE08\uC735\uC5C5\uBB34\uB97C \uC81C\uACF5\uD558\uACE0, \uD68C\uC6D0\uC774 \uD68C\uC0AC\uC758 \uC885\uC0AC\uC790\uC640 \uC9C1\uC811 \uB300\uBA74\uD558\uAC70\uB098 \uC758\uC0AC\uC18C\uD1B5\uC744 \uD558\uC9C0 \uC544\uB2C8\uD558\uACE0 \uC790\uB3D9\uD654\uB41C \uBC29\uC2DD\uC73C\uB85C \uC774\uB97C \uC774\uC6A9\uD558\uB294 \uAC70\uB798\uB97C \uB9D0\uD569\uB2C8\uB2E4.\n2. \u2018\uD68C\uC6D0\u2019\uC774\uB77C \uD568\uC740 \uBCF8 \uC57D\uAD00\uC5D0 \uB3D9\uC758\uD558\uACE0 \uBCF8 \uC57D\uAD00\uC5D0 \uB530\uB77C \uD68C\uC0AC\uAC00 \uC81C\uACF5\uD558\uB294 \uC804\uC790\uAE08\uC735\uAC70\uB798\uC11C\uBE44\uC2A4\uB97C \uC774\uC6A9\uD558\uB294 \uC774\uC6A9\uC790\uB97C \uB9D0\uD569\uB2C8\uB2E4.\n3. \u2018\uAC1C\uC124\uC790\u2019\uB77C \uD568\uC740 \uAC1C\uC124\uB41C \uD589\uC0AC\uC758 \uC8FC\uCD5C\uC790\uB97C \uC774\uC57C\uAE30\uD569\uB2C8\uB2E4.\n4. \u2018\uC2E0\uCCAD\uC790\u2019\uB77C \uD568\uC740 \uAC1C\uC124\uB41C \uD589\uC0AC\uC758 \uCC38\uC5EC\uC790\uB97C \uC774\uC57C\uAE30\uD569\uB2C8\uB2E4.\n5. \u2018\uB300\uAE30\uC790\u2019\uB77C \uD568\uC740 \uACB0\uC81C\uB97C \uC644\uB8CC\uD558\uC600\uC73C\uB098 \uD589\uC0AC\uC758 \uC811\uC218\uBC29\uBC95, \uC2E0\uCCAD\uD604\uD669 \uB4F1\uC5D0 \uB530\uB77C \uB300\uAE30\uC790\uB85C \uC2E0\uCCAD\uB41C \uC790\uB97C \uC774\uC57C\uAE30\uD569\uB2C8\uB2E4.\n\u2461 \uBCF8 \uC870 \uBC0F \uBCF8 \uC57D\uAD00\uC758 \uB2E4\uB978 \uC870\uD56D\uC5D0\uC11C \uC815\uC758\uD55C \uAC83\uC744 \uC81C\uC678\uD558\uACE0\uB294 \uC804\uC790\uAE08\uC735\uAC70\uB798\uBC95, \uC804\uC790\uC0C1\uAC70\uB798 \uB4F1\uC5D0\uC11C\uC758 \uC18C\uBE44\uC790\uBCF4\uD638\uC5D0 \uAD00\uD55C \uBC95\uB960 \uB4F1 \uAD00\uB828 \uBC95\uB839\uC5D0 \uC815\uD55C \uBC14\uC5D0 \uB530\uB985\uB2C8\uB2E4.\n\uC81C3\uC870 (\uCDE8\uC18C/\uD658\uBD88 \uC2E0\uCCAD \uAE30\uAC04)\n\u2460 \uD68C\uC0AC \uC804\uC790\uAE08\uC735\uAC70\uB798 \uC11C\uBE44\uC2A4\uB97C \uC774\uC6A9\uD558\uB294 \uC720\uB8CC\uD589\uC0AC\uC758 \uCDE8\uC18C/\uD658\uBD88 \uC2E0\uCCAD\uAE30\uAC04\uC740 \uAC1C\uC124\uC790\uAC00 \uC124\uC815\uD55C \uD589\uC0AC\uC758 \uC2E0\uCCAD\uAE30\uAC04\uACFC \uB3D9\uC77C\uD569\uB2C8\uB2E4.\n\u2461 \uC2E0\uCCAD\uAE30\uAC04 \uB9C8\uAC10 \uC774\uD6C4\uC5D0\uB294 \uD68C\uC0AC\uC5D0\uC11C \uCDE8\uC18C/\uBCC0\uACBD/\uD658\uBD88\uC774 \uBD88\uAC00\uB2A5\uD569\uB2C8\uB2E4.\n\u2462 \uC2E0\uCCAD\uAE30\uAC04 \uB9C8\uAC10 \uC774\uD6C4\uC758 \uCDE8\uC18C/\uBCC0\uACBD/\uD658\uBD88\uC740 \uAC1C\uC124\uC790\uC5D0\uAC8C \uBB38\uC758\uD574\uC57C \uD558\uBA70, \uAC1C\uC124\uC790\uC758 \uACB0\uC815\uC5D0 \uB530\uB985\uB2C8\uB2E4.\n\u2463 \uC2E0\uCCAD\uAE30\uAC04\uC740 \uAC1C\uC124\uC790\uC758 \uC124\uC815\uC5D0 \uC758\uD558\uC5EC \uBCC0\uACBD\uB420 \uC218 \uC788\uC2B5\uB2C8\uB2E4.\n\uC81C4\uC870 (\uCDE8\uC18C/\uD658\uBD88 \uBC29\uBC95)\n\u2460 \uC2E0\uCCAD\uC790\uB294 \uC2E0\uCCAD\uAE30\uAC04 \uB9C8\uAC10 \uC804\uAE4C\uC9C0 \uC544\uB798\uC758 \uBC29\uBC95\uC73C\uB85C \uD658\uBD88\uC744 \uC774\uC6A9\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.\n\uAC00. \uB85C\uADF8\uC778 \u2192 \uAD00\uB9AC \u2192 \uACB0\uC81C\uB0B4\uC5ED \u2192 \uC2E0\uCCAD\uB0B4\uC5ED \u2192 \uACB0\uC81C\uCDE8\uC18C\n\uB098. \uACE0\uAC1D\uC13C\uD130 \uBB38\uC758\uD558\uAE30\n\u2461 \uACB0\uC81C\uC218\uB2E8, \uD658\uBD88\uC2E0\uCCAD \uC2DC\uC810, \uD658\uBD88 \uC0AC\uC720\uC5D0 \uB530\uB77C \uD658\uBD88 \uC218\uC218\uB8CC\uAC00 \uBD80\uACFC\uB420 \uC218 \uC788\uC2B5\uB2C8\uB2E4.\n\u2462 \uACB0\uC81C\uC218\uB2E8, \uD658\uBD88 \uC2E0\uCCAD \uC2DC\uC810\uC5D0 \uB530\uB77C \uD658\uBD88 \uBC29\uC2DD\uC740 \uACB0\uC81C\uCDE8\uC18C \uB610\uB294 \uD658\uBD88\uC2E0\uCCAD\uC73C\uB85C \uB2E4\uB97C \uC218 \uC788\uC2B5\uB2C8\uB2E4.\n\uAC00. \uACB0\uC81C\uCDE8\uC18C\uB294 PG\uC0AC\uB97C \uD1B5\uD574 \uC790\uB3D9 \uD658\uBD88 \uCC98\uB9AC\uAC00 \uB418\uB294 \uBC29\uC2DD\uC785\uB2C8\uB2E4.\n\uB098. \uD658\uBD88\uC2E0\uCCAD\uC740 \uD68C\uC0AC \uACE0\uAC1D\uC13C\uD130\uB85C \uD658\uBD88 \uACC4\uC88C\uB97C \uC811\uC218\uD558\uC5EC \uACC4\uC88C\uC785\uAE08 \uB418\uB294 \uD658\uBD88 \uBC29\uC2DD\uC785\uB2C8\uB2E4.\n\u2463 \uACB0\uC81C \uC218\uB2E8\uBCC4 \uD658\uBD88 \uC2DC\uC810 \uBC0F \uD658\uBD88\uBC29\uC2DD\uC740 \uC544\uB798\uC640 \uAC19\uC2B5\uB2C8\uB2E4.\n\uAC00. \uC2E0\uC6A9\uCE74\uB4DC\n1) \uD68C\uC0AC\uC5D0\uC11C\uC758 \uD658\uBD88 \uCC98\uB9AC\uAC00 \uC644\uB8CC\uB418\uACE0 \uCE74\uB4DC\uC0AC\uB97C \uD1B5\uD574 \uACB0\uC81C\uC2B9\uC778\uC774 \uCDE8\uC18C\uB418\uBA74 \uD658\uBD88(\uD55C\uB3C4\uC561 \uBCF5\uAD6C)\uAC00 \uC644\uB8CC\uB429\uB2C8\uB2E4.\n2) \uACB0\uC81C\uB2F9\uC77C \uC774\uD6C4 \uACB0\uC81C\uCDE8\uC18C \uC2DC \uCE74\uB4DC\uC0AC\uB97C \uD1B5\uD55C \uACB0\uC81C\uC2B9\uC778 \uCDE8\uC18C \uD655\uC778\uC740 3~7\uC77C \uAC00\uB7C9 \uC18C\uC694\uB420 \uC218 \uC788\uC73C\uBA70, \uC774\uB294 \uCE74\uB4DC\uC0AC\uB9C8\uB2E4 \uB2E4\uB97C \uC218 \uC788\uC2B5\uB2C8\uB2E4.\n\uB098. \uC2E4\uC2DC\uAC04 \uACC4\uC88C\uC774\uCCB4\n1) \uD68C\uC0AC\uC5D0\uC11C\uC758 \uD658\uBD88 \uCC98\uB9AC\uAC00 \uC644\uB8CC\uB418\uBA74 \uACB0\uC81C\uC5D0 \uC774\uC6A9\uD55C \uACC4\uC88C\uB85C \uC790\uB3D9 \uC785\uAE08\uB418\uBA70, \uD3C9\uC77C \uAE30\uC900 1~2\uC77C \uAC00\uB7C9 \uC18C\uC694\uB420 \uC218 \uC788\uC2B5\uB2C8\uB2E4.\n2) \uC785\uAE08 \uC2DC\uC810\uC740 \uC740\uD589\uC0AC\uC758 \uC0AC\uC815\uC5D0 \uB530\uB77C \uB2EC\uB77C\uC9C8 \uC218 \uC788\uC2B5\uB2C8\uB2E4.\n\uB2E4. \uBB34\uD1B5\uC7A5\uC785\uAE08(\uAC00\uC0C1\uACC4\uC88C), \uD734\uB300\uC804\uD654\uACB0\uC81C, \uBCC4\uB3C4 \uACC4\uC88C\uC785\uAE08\n1) \uD658\uBD88\uC740 \uD658\uBD88 \uACC4\uC88C \uC811\uC218 \uD6C4 5~7\uC77C \uB0B4\uC5D0 \uC811\uC218\uB41C \uACC4\uC88C\uB85C \uCDE8\uC18C/\uD658\uBD88\uC218\uC218\uB8CC\uB97C \uACF5\uC81C\uD55C \uAE08\uC561\uC73C\uB85C \uC785\uAE08\uB429\uB2C8\uB2E4.\n2) \uC811\uC218 \uACC4\uC88C \uC624\uB958, \uC99D\uBE59\uC11C\uB958 \uBBF8\uC811\uC218 \uB4F1\uC758 \uC0AC\uC720\uB85C \uD658\uBD88\uC774 \uC720\uBCF4 \uB610\uB294 \uC9C0\uC5F0\uB420 \uC218 \uC788\uC73C\uBA70, \uC774 \uACBD\uC6B0 \uC774\uBA54\uC77C \uB4F1\uC744 \uD1B5\uD574 \uBCC4\uB3C4 \uC548\uB0B4\uD569\uB2C8\uB2E4.\n\uB77C. \uD658\uBD88 \uACC4\uC88C\uC758 \uC811\uC218\n1) \uD658\uBD88\uC740 \uBC18\uB4DC\uC2DC \uC2E0\uCCAD\uC790 \uBCF8\uC778 \uBA85\uC758\uC758 \uACC4\uC88C\uB85C \uC811\uC218\uD574\uC57C \uD569\uB2C8\uB2E4.\n\uC2E0\uCCAD\uC790\uC640 \uBA85\uC758\uAC00 \uC77C\uCE58\uD558\uC9C0 \uC54A\uC744 \uACBD\uC6B0 \uD68C\uC0AC\uB294 \uC99D\uBE59\uC11C\uB958\uB97C \uC694\uCCAD\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.\n2) \uC99D\uBE59\uC11C\uB958\uB294 \uD589\uC0AC\uB97C \uC2E0\uCCAD\uD55C \uC544\uC774\uB514(\uC774\uBA54\uC77C)\uB85C\uB9CC \uC811\uC218\uBC1B\uC73C\uBA70 \uB2E4\uB978 \uC774\uBA54\uC77C\uB85C \uC811\uC218\uD558\uAC70\uB098 \uC99D\uBE59\uC11C\uB958 \uBBF8\uC811\uC218 \uC2DC \uD658\uBD88\uC774 \uC720\uBCF4\uB420 \uC218 \uC788\uC2B5\uB2C8\uB2E4.\n3) \uC798\uBABB\uB41C \uD658\uBD88 \uACC4\uC88C \uB610\uB294 \uC99D\uBE59\uC11C\uB958\uB97C \uC811\uC218\uD558\uC5EC \uBC1C\uC0DD\uD55C \uBB38\uC81C\uC5D0 \uB300\uD558\uC5EC \uD68C\uC0AC\uB294 \uCC45\uC784\uC9C0\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.\n\u203B \uD0C0\uBA85\uC758 \uACC4\uC88C\uB85C\uC758 \uD658\uBD88 \uC811\uC218 \uC2DC \uC99D\uBE59\uC11C\uB958\n* \uAC1C\uC778\uACC4\uC88C : \uBA85\uC758\uAC00 \uC77C\uCE58\uD558\uB294 \uC2E0\uBD84\uC99D \uC0AC\uBCF8, \uD1B5\uC7A5 \uC0AC\uBCF8\n* \uC0AC\uC5C5\uC790\uACC4\uC88C : \uBA85\uC758\uAC00 \uC77C\uCE58\uD558\uB294 \uC0AC\uC5C5\uC790\uB4F1\uB85D\uC99D \uC0AC\uBCF8, \uD1B5\uC7A5 \uC0AC\uBCF8\n* \uACB0\uC81C\uC5D0 \uC774\uC6A9\uD55C \uACC4\uC88C\uB85C \uD658\uBD88\uC744 \uD76C\uB9DD\uD560 \uACBD\uC6B0 : \uC740\uD589\uBA85, \uACC4\uC88C\uBC88\uD638\uAC00 \uD655\uC778\uB418\uB294 \uC785\uAE08\uD655\uC778\uC99D\n\uC81C5\uC870 (\uCDE8\uC18C/\uD658\uBD88 \uC218\uC218\uB8CC)\n\u2460\uCDE8\uC18C/\uD658\uBD88 \uC218\uC218\uB8CC\uB294 \uACB0\uC81C\uC218\uB2E8, \uD658\uBD88\uC2DC\uC810, \uC2E0\uCCAD\uC0C1\uD0DC, \uD658\uBD88\uC0AC\uC720\uC5D0 \uB530\uB77C \uB2E4\uB974\uAC8C \uC801\uC6A9 \uBC0F \uACF5\uC81C\uB429\uB2C8\uB2E4.\n\u2461\uCDE8\uC18C/\uD658\uBD88 \uC218\uC218\uB8CC \uACF5\uC81C\uC5D0 \uB300\uD55C \uC138\uAE08\uACC4\uC0B0\uC11C \uB610\uB294 \uD604\uAE08\uC601\uC218\uC99D\uC744 \uD76C\uB9DD\uD558\uB294 \uACBD\uC6B0 \uACE0\uAC1D\uC13C\uD130\uB85C \uBB38\uC758 \uBC14\uB78D\uB2C8\uB2E4.\n\uAC00. \uCC38\uAC00\uC790 \uD658\uBD88\n1) \uCC38\uAC00\uC790 \uC0C1\uD0DC\uC758 \uC2E0\uCCAD\uC790\uAC00 \uB2E8\uC21C \uBCC0\uC2EC \uB4F1 \uBCF8\uC778\uC758 \uC0AC\uC815\uC73C\uB85C \uC778\uD574 \uD658\uBD88\uC744 \uD560 \uACBD\uC6B0\uC5D0 \uD574\uB2F9\uD569\uB2C8\uB2E4.\n2) \uBB34\uD1B5\uC7A5\uC785\uAE08(\uAC00\uC0C1\uACC4\uC88C)\uB294 \uACB0\uC81C\uAE08\uC561\uC774 1,100\uC6D0 \uBBF8\uB9CC\uC77C \uACBD\uC6B0 \uD658\uBD88\uC774 \uBD88\uAC00\uB2A5\uD569\uB2C8\uB2E4.\n3) \uD734\uB300\uC804\uD654\uACB0\uC81C \uD6C4 \uBC88\uD638\uC774\uB3D9, \uD574\uC9C0 \uB4F1 \uD734\uB300\uC804\uD654 \uC815\uBCF4\uAC00 \uBCC0\uACBD\uB41C \uACBD\uC6B0 \uACB0\uC81C\uC77C\uC790, \uC2E0\uCCAD\uC0C1\uD0DC, \uD658\uBD88\uC2DC\uC810\uC5D0 \uB530\uB77C \uC57D\uAD00 \uB0B4\uC6A9\uC774 \uC801\uC6A9\uB429\uB2C8\uB2E4.\n\n\uACB0\uC81C\uB2F9\uC77C    \uACB0\uC81C\uC775\uC77C ~ \uCC38\uC5EC\uC2E0\uCCAD\uAE30\uAC04 \uB9C8\uAC10 \uC804  \uCC38\uC5EC\uC2E0\uCCAD\uAE30\uAC04 \uB9C8\uAC10 \uD6C4\n\uC2E0\uC6A9\uCE74\uB4DC    \uC218\uC218\uB8CC \uC5C6\uC74C\n\uC2E4\uC2DC\uAC04\uACC4\uC88C\uC774\uCCB4 \uC218\uC218\uB8CC \uC5C6\uC74C  10% (\uC2E0\uCCAD\uCDE8\uC18C\uC218\uC218\uB8CC)   10% (\uC2E0\uCCAD\uCDE8\uC18C\uC218\uC218\uB8CC)\n\uBB34\uD1B5\uC7A5\uC785\uAE08\n(\uAC00\uC0C1\uACC4\uC88C)  \uC218\uC218\uB8CC \uC5C6\uC74C  10% (\uC2E0\uCCAD\uCDE8\uC18C\uC218\uC218\uB8CC)   10% (\uC2E0\uCCAD\uCDE8\uC18C\uC218\uC218\uB8CC)\n\uB098. \uB300\uAE30\uC790 \uD658\uBD88\n1) \uD589\uC0AC\uAE30\uAC04 \uC885\uB8CC \uD6C4 \uD68C\uC0AC\uC5D0\uC11C \uACB0\uC81C\uC644\uB8CC \uB300\uAE30\uC790\uC758 \uD658\uBD88 \uC5EC\uBD80\uB97C \uBCC4\uB3C4 \uD655\uC778\uD558\uC5EC \uC548\uB0B4\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4.\n2) \uB300\uAE30\uC790\uB294 \uACB0\uC81C\uC218\uB2E8, \uD658\uBD88\uC2DC\uC810\uC5D0 \uAD00\uACC4\uC5C6\uC774 \uD658\uBD88 \uC218\uC218\uB8CC\uAC00 \uBD80\uACFC\uB418\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.\n\uB2E4. \uACB0\uC81C\uC218\uB2E8 \uBCC0\uACBD \uD658\uBD88\n1) \uC774\uC804 \uACB0\uC81C \uC2DC \uC774\uC6A9\uD55C \uACB0\uC81C\uC218\uB2E8\uC5D0 \uB530\uB77C \uD658\uBD88 \uC218\uC218\uB8CC\uAC00 \uBD80\uACFC\uB420 \uC218 \uC788\uC2B5\uB2C8\uB2E4.\n2) \uD734\uB300\uC804\uD654\uACB0\uC81C \uD6C4 \uBC88\uD638\uC774\uB3D9, \uD574\uC9C0 \uB4F1 \uD734\uB300\uC804\uD654 \uC815\uBCF4\uAC00 \uBCC0\uACBD\uB41C \uACBD\uC6B0 \uACB0\uC81C\uC77C\uC790\uC640 \uAD00\uACC4\uC5C6\uC774 \u2018\uACB0\uC81C \uC775LANG(\"month\")\u2019 \uD658\uBD88 \uC218\uC218\uB8CC\uAC00 \uC801\uC6A9\uB429\uB2C8\uB2E4.\n\uC2E0\uC6A9\uCE74\uB4DC    \uC2E4\uC2DC\uAC04 \uACC4\uC88C\uC774\uCCB4, \uBCC4\uB3C4 \uACC4\uC88C\uC785\uAE08   \uBB34\uD1B5\uC7A5\uC785\uAE08\n(\uAC00\uC0C1\uACC4\uC88C)  \uD734\uB300\uC804\uD654\n(\uACB0\uC81C \uB2F9LANG(\"month\")) \uD734\uB300\uC804\uD654\n(\uACB0\uC81C \uC775LANG(\"month\"))\n\uC218\uC218\uB8CC \uC5C6\uC74C  \uACB0\uC81C\uAE08\uC561\uC758 1.7%  \uACB0\uC81C\uAE08\uC561\uC758 2.5%  \uC218\uC218\uB8CC \uC5C6\uC74C  \uACB0\uC81C\uAE08\uC561\uC758 4%\n\uC81C6\uC870 (\uD589\uC0AC \uAC1C\uC124\uC790\uC758 \uD589\uC0AC \uC911\uB3C4 \uCDE8\uC18C/\uBCC0\uACBD \uC2DC \uD658\uBD88)\n\u2460 \uD68C\uC0AC \uACB0\uC81C\uC11C\uBE44\uC2A4\uB97C \uC774\uC6A9\uD558\uB294 \uC720\uB8CC\uD589\uC0AC\uB97C \uAC1C\uC124\uD558\uC5EC \uCC38\uAC00\uBE44 \uACB0\uC81C\uAC00 \uBC1C\uC0DD\uD55C \uD6C4, \uAC1C\uC124\uC790\uC758 \uC0AC\uC815\uC73C\uB85C \uC778\uD574 \uD589\uC0AC\uAC00 \uC911\uB3C4 \uBCC0\uACBD \uB610\uB294 \uCDE8\uC18C \uB4F1\uC774 \uC774\uB8E8\uC5B4\uC9C8 \uACBD\uC6B0 \uC2E0\uCCAD\uC790\uC5D0 \uB300\uD55C \uD658\uBD88 \uC218\uC218\uB8CC\uB294 \uAC1C\uC124\uC790\uAC00 \uBD80\uB2F4\uD569\uB2C8\uB2E4.\n\u2461 \uAC1C\uC124\uC790\uAC00 \uD68C\uC0AC\uB85C \uCDE8\uC18C/\uD658\uBD88 \uC218\uC218\uB8CC \uC785\uAE08 \uD6C4\uC5D0 \uC2E0\uCCAD\uC790\uC5D0 \uB300\uD55C \uD658\uBD88 \uC808\uCC28\uAC00 \uC9C4\uD589\uB429\uB2C8\uB2E4.\n\u2462 \uC2E0\uCCAD\uC790\uC5D0 \uB300\uD55C \uD658\uBD88\uC740 \uC2E0\uCCAD\uC790\uAC00 \uC774\uC6A9\uD55C \uACB0\uC81C\uC218\uB2E8\uACFC \uACB0\uC81C\uC77C\uC2DC\uC5D0 \uAD00\uACC4 \uC5C6\uC774 \uC804\uC561 \uD658\uBD88\uB429\uB2C8\uB2E4.\n\uAC00. \uD589\uC0AC \uC911\uB3C4 \uCDE8\uC18C/\uBCC0\uACBD \uD658\uBD88 \uC808\uCC28\n1) \uAC1C\uC124\uC790\uAC00 \uC2E0\uCCAD\uC790(\uCC38\uAC00\uC790, \uB300\uAE30\uC790)\uC5D0\uAC8C \uD589\uC0AC \uCDE8\uC18C/\uBCC0\uACBD \uC0AC\uC2E4 \uC548\uB0B4 \uBC0F \uD658\uBD88 \uC808\uCC28 \uC548\uB0B4\n2) \uD589\uC0AC \uC2E0\uCCAD\uAE30\uAC04 \uB9C8\uAC10 \uCC98\uB9AC\n3) \uD68C\uC0AC \uACE0\uAC1D\uC13C\uD130\uB85C \uCDE8\uC18C/\uD658\uBD88\uC218\uC218\uB8CC \uC785\uAE08 \uBC0F \uC2E0\uCCAD\uC790 \uD658\uBD88 \uC2E0\uCCAD \uC811\uC218\n\uB098. \uC720\uB8CC\uD589\uC0AC \uC911\uB3C4 \uCDE8\uC18C/\uBCC0\uACBD \uC218\uC218\uB8CC\n\uACB0\uC81C\uC218\uB2E8\n\uC218\uC218\uB8CC\n\uACB0\uC81C\uC218\uB2E8\n\uC218\uC218\uB8CC\n\uC2E0\uC6A9\uCE74\uB4DC(\uAD6D\uB0B4/\uD574\uC678) \uC218\uC218\uB8CC \uC5C6\uC74C\n\uC2E4\uC2DC\uAC04 \uACC4\uC88C\uC774\uCCB4    \uC2E4\uC2DC\uAC04\uACC4\uC88C\uC774\uCCB4 \uCD1D \uACB0\uC81C\uAE08\uC561\uC758 2% X \uC2E0\uCCAD\uC778\uC6D0\n\uBB34\uD1B5\uC7A5\uC785\uAE08(\uAC00\uC0C1\uACC4\uC88C) 800\uC6D0 X \uC2E0\uCCAD\uC778\uC6D0\n(\uC2DC\uD589\uC77C) \uBCF8 \uC57D\uAD00\uC740 2019 \uB144 2LANG(\"month\") 26\uC77C \uBD80\uD130 \uC2DC\uD589\uB429\uB2C8\uB2E4.";
