  // Í≤ÄÏÇ¨Ìïú Í≤∞Í≥ºÎ•º ValidationÏóê Í∏∞ÏûÖ.

  const resizeBlockBlock: TResizeBlockBlock = async (
    targetGuest: IAssigItem,
    time: number
