  const groupToRoomType = (
    createItemTempGroups: IAssigGroup[]
  ): getBooking_GetBooking_booking_roomTypes[] => {
    const uniquRoomTypes = _.uniqBy(
      createItemTempGroups,
      group => group.roomTypeId
