  };

  // üëº Ïª¥Ìè¨ÎÑåÌä∏Îì§ ÎÇ¥Î∂ÄÏóê propÎ•º Ï†ÑÎã¨ÌïòÍ∏∞ ÌûòÎìúÎãàÍπå Ïù¥Î†áÍ≤å Ï†ÑÎã¨ÌïòÏûê.
  const assigUtils: IAssigTimelineUtils = {
    changeMarkToGhost,
    getItemById,
    getGroupById,
    removeMark,
    getInfoesFromMarks,
    isTherePerson,
    filterTimeZone,
    itemsToGuets,
    groupToRoomType,
    allTooltipsHide,
    deleteItemById,
    // openCreateMenu,
    isGenderSafe,
    getGuestsByBookingId,
    addBlock,
    allocateGuest,
    allocateItem,
    resizeBlockBlock,
    genderToggleById,
    resizeValidater,
    bookingCheckedNew,
    resizeLinkedItems,
    moveLinkedItems,
    toogleCheckInOut,
    openBlockMenu,
